import "./App.css";
// Importando la imagen
import freeCodeCampLogo from "./imgs/logo.svg";
// Importando el componente de boton
import { Boton } from "./components/Boton";
// Importando el componente Pantalla
import Pantalla from "./components/Pantalla";
// Importando el componente de Boton Clear
import BotonClear from "./components/BotonClear";
// Importando use state
import { useState } from "react";

function App() {

  // Hooks
  const [input, setInput] = useState("");



  return (
    <div className="App">
      <div className="freecodecamp-logo-contenedor">
        <img
          // Llamando a la imagen
          src={freeCodeCampLogo}
          className="freecodecamp-logo"
          alt="Logo de freecodecamp"
        />
      </div>
      <div className="contenedor-calculadora">
        <Pantalla />
        <div className="fila">
          <Boton>1</Boton>
          <Boton>2</Boton>
          <Boton>3</Boton>
          <Boton>+</Boton>
        </div>
        <div className="fila">
          <Boton>4</Boton>
          <Boton>5</Boton>
          <Boton>6</Boton>
          <Boton>-</Boton>
        </div>
        <div className="fila">
          <Boton>7</Boton>
          <Boton>8</Boton>
          <Boton>9</Boton>
          <Boton>*</Boton>
        </div>
        <div className="fila">
          <Boton>=</Boton>
          <Boton>0</Boton>
          <Boton>.</Boton>
          <Boton>/</Boton>
        </div>
        <div className="fila">
          <BotonClear>Clear</BotonClear>
        </div>
      </div>
    </div>
  );
}

export default App;
